    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        // TODO add your handling code here:
        //save here to the Database! 
        if(!"".equals(txtSubjectName.getText().trim())){
            if(!"".equals(txtSubjectCode.getText().trim())){
                int subjectId=Integer.parseInt(txtSubjectCode.getText().trim());
                String subjectName=txtSubjectName.getText().trim();
                if(Database_Operations.addSubject(Login.username,subjectId, subjectName)){
           //also create a new table with the name of subject+subject_id!
                    JOptionPane.showMessageDialog(null,"Record inserted Successfully!");
                }else{
                    JOptionPane.showMessageDialog(null,"Record not inserted!");
                }
                java.awt.EventQueue.invokeLater(new Runnable() {
                    public void run() {
                        new ChoosingSubject().setVisible(true);
                    }
                });
                 dispose();
            }else{
                 JOptionPane.showMessageDialog(null,"Please enter Subject Code");
                 return;
            }
        }else{
             JOptionPane.showMessageDialog(null,"Please enter Subject Name");
        }
    }//GEN-LAST:event_btnSaveActionPerformed

