    private void txtSubjectCodeKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSubjectCodeKeyPressed
        // TODO add your handling code here:
         int key = evt.getKeyCode();
        if (key < 96 && key > 105) {
             evt.setKeyChar(' ');
        }
    }//GEN-LAST:event_txtSubjectCodeKeyPressed

